// src/components/ListaProduto.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './ListaProdutos.css';

const ListaProduto = () => {
    const [produtos, setProdutos] = useState([]);

    useEffect(() => {
        const fetchProdutos = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:5000/produtos');
                setProdutos(response.data);  // Armazena os dados retornados na variável produtos
            } catch (error) {
                console.error('Erro ao buscar produtos:', error);
            }
        };
        fetchProdutos();
    }, []);  // O array vazio garante que isso seja executado apenas uma vez ao carregar a página

    return (
        <div className="table-container">
            <h2>Lista de Produtos</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Nome</th>
                        <th>Preço</th>
                        <th>Quantidade</th>
                        <th>Usuário</th>  {/* Adicionando a coluna para o nome do usuário */}
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {produtos.length > 0 ? (
                        produtos.map((produto) => (
                            <tr key={produto.id}>
                                <td>{produto.nome}</td>
                                <td>{produto.preco}</td>
                                <td>{produto.quantidade}</td>
                                <td>{produto.usuario}</td>  {/* Exibindo o nome do usuário que cadastrou o produto */}
                                <td>
                                    <Link to={`/editar-produto/${produto.id}`} className="button">
                                        Editar
                                    </Link>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="5">Nenhum produto encontrado</td>  {/* Mensagem caso não haja produtos */}
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ListaProduto;
