// src/components/EditUser.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './EditUser.css';

const EditUser = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [usuario, setUsuario] = useState({
        nome: '',
        email: '',
        cpf_cnpj: '',
        senha: '',
    });
    const [mensagem, setMensagem] = useState('');

    useEffect(() => {
        const fetchUsuario = async () => {
            try {
                const response = await axios.get(`http://127.0.0.1:5000/usuarios/${id}`);
                setUsuario(response.data);
            } catch (error) {
                console.error('Erro ao buscar usuário:', error);
            }
        };
        fetchUsuario();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUsuario((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://127.0.0.1:5000/usuarios/${id}`, usuario);
            setMensagem('Usuário atualizado com sucesso!');
            navigate('/usuarios'); // Redirecionar para a lista de usuários após a atualização
        } catch (error) {
            console.error('Erro ao atualizar usuário:', error);
            setMensagem('Erro ao atualizar usuário.');
        }
    };

    return (
        <div className="form-container">
            <h2>Editar Usuário</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label className="label">Nome</label>
                    <input
                        type="text"
                        name="nome"
                        className="input"
                        value={usuario.nome}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label className="label">Email</label>
                    <input
                        type="email"
                        name="email"
                        className="input"
                        value={usuario.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label className="label">CPF/CNPJ</label>
                    <input
                        type="text"
                        name="cpf_cnpj"
                        className="input"
                        value={usuario.cpf_cnpj}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label className="label">Senha</label>
                    <input
                        type="password"
                        name="senha"
                        className="input"
                        value={usuario.senha}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit" className="button">Atualizar</button>
            </form>
            {mensagem && <p>{mensagem}</p>} {/* Exibe a mensagem */}
        </div>
    );
};

export default EditUser;
