// src/components/ListaProdutos.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ListaProdutos.css';

const ListaProdutos = () => {
    const [produtos, setProdutos] = useState([]);

    useEffect(() => {
        const fetchProdutos = async () => {
            const response = await axios.get('http://127.0.0.1:5000/produtos');
            setProdutos(response.data);
        };
        fetchProdutos();
    }, []);

    const handleEdit = async (id) => {
        const nome = prompt("Nome do produto:");
        const preco = prompt("Preço do produto:");
        const descricao = prompt("Descrição do produto:");

        if (nome && preco && descricao) {
            await axios.put(`http://127.0.0.1:5000/produtos/${id}`, {
                nome, preco, descricao
            });
            setProdutos(produtos.map(produto => produto.id === id ? { ...produto, nome, preco, descricao } : produto));
        }
    };

    return (
        <div className="lista-container">
            <h2>Lista de Produtos</h2>
            <table className="produtos-tabela">
                <thead>
                    <tr>
                        <th>Nome</th>
                        <th>Preço</th>
                        <th>Descrição</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {produtos.map((produto) => (
                        <tr key={produto.id}>
                            <td>{produto.nome}</td>
                            <td>{produto.preco}</td>
                            <td>{produto.descricao}</td>
                            <td>
                                <button onClick={() => handleEdit(produto.id)} className="button editar-button">Editar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListaProdutos;
