// src/components/AddSale.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddSale = () => {
    const [produtos, setProdutos] = useState([]);
    const [usuarios, setUsuarios] = useState([]);
    const [venda, setVenda] = useState({
        produto_id: '',
        vendedor_id: '',
        comprador_id: '',
        quantidade: ''
    });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const produtosRes = await axios.get('http://127.0.0.1:5000/produtos');
                const usuariosRes = await axios.get('http://127.0.0.1:5000/usuarios');
                setProdutos(produtosRes.data);
                setUsuarios(usuariosRes.data);
            } catch (error) {
                console.error('Erro ao buscar dados:', error);
            }
        };
        fetchData();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setVenda({ ...venda, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://127.0.0.1:5000/venda', venda);
            alert('Venda cadastrada com sucesso!');
            setVenda({ produto_id: '', vendedor_id: '', comprador_id: '', quantidade: '' });
        } catch (error) {
            console.error('Erro ao cadastrar venda:', error);
        }
    };

    return (
        <div>
            <h2>Cadastrar Venda</h2>
            <form onSubmit={handleSubmit}>
                <select name="produto_id" value={venda.produto_id} onChange={handleChange} required>
                    <option value="">Selecione o produto</option>
                    {produtos.map((produto) => (
                        <option key={produto.id} value={produto.id}>
                            {produto.nome} - Estoque: {produto.quantidade}
                        </option>
                    ))}
                </select>

                <select name="vendedor_id" value={venda.vendedor_id} onChange={handleChange} required>
                    <option value="">Selecione o vendedor</option>
                    {usuarios.map((usuario) => (
                        <option key={usuario.id} value={usuario.id}>
                            {usuario.nome}
                        </option>
                    ))}
                </select>

                <select name="comprador_id" value={venda.comprador_id} onChange={handleChange} required>
                    <option value="">Selecione o comprador</option>
                    {usuarios.map((usuario) => (
                        <option key={usuario.id} value={usuario.id}>
                            {usuario.nome}
                        </option>
                    ))}
                </select>

                <input
                    type="number"
                    name="quantidade"
                    value={venda.quantidade}
                    onChange={handleChange}
                    placeholder="Quantidade"
                    required
                />

                <button type="submit">Cadastrar Venda</button>
            </form>
        </div>
    );
};

export default AddSale;
