// src/components/EditProduct.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditProduct = () => {
    const { id } = useParams();
    const [produto, setProduto] = useState({ nome: '', preco: '', quantidade: '' });
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProduto = async () => {
            const response = await axios.get(`http://127.0.0.1:5000/produto/${id}`);
            setProduto(response.data);
        };
        fetchProduto();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.put(`http://127.0.0.1:5000/produto/${id}`, produto);
        navigate('/produtos'); // Redireciona para a lista de produtos após a edição
    };

    return (
        <div>
            <h2>Editar Produto</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Nome:</label>
                    <input
                        type="text"
                        value={produto.nome}
                        onChange={(e) => setProduto({ ...produto, nome: e.target.value })}
                    />
                </div>
                <div>
                    <label>Preço:</label>
                    <input
                        type="number"
                        value={produto.preco}
                        onChange={(e) => setProduto({ ...produto, preco: e.target.value })}
                    />
                </div>
                <div>
                    <label>Quantidade:</label>
                    <input
                        type="number"
                        value={produto.quantidade}
                        onChange={(e) => setProduto({ ...produto, quantidade: e.target.value })}
                    />
                </div>
                <button type="submit">Atualizar Produto</button>
            </form>
        </div>
    );
};

export default EditProduct;
