// src/components/UserList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './UserList.css';

const UserList = () => {
    const [usuarios, setUsuarios] = useState([]);

    useEffect(() => {
        const fetchUsuarios = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:5000/usuarios');
                setUsuarios(response.data);
            } catch (error) {
                console.error('Erro ao listar usuários:', error);
            }
        };
        fetchUsuarios();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://127.0.0.1:5000/usuarios/${id}`);
            setUsuarios(usuarios.filter((usuario) => usuario.id !== id));
        } catch (error) {
            console.error('Erro ao deletar usuário:', error);
        }
    };

    return (
        <div className="user-list-container">
            <h2>Lista de Usuários</h2>
            <table className="user-list-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nome</th>
                        <th>Email</th>
                        <th>CPF/CNPJ</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {usuarios.map((usuario) => (
                        <tr key={usuario.id}>
                            <td>{usuario.id}</td>
                            <td>{usuario.nome}</td>
                            <td>{usuario.email}</td>
                            <td>{usuario.cpf_cnpj}</td>
                            <td>
                                <Link to={`/editar/${usuario.id}`} className="button">
                                    Editar
                                </Link>
                                <button onClick={() => handleDelete(usuario.id)} className="button">
                                    Deletar
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserList;
