import React, { useState } from 'react';
import axios from 'axios';

const AddSale = () => {
    const [selectedProductId, setSelectedProductId] = useState(null);
    const [quantidade, setQuantidade] = useState('');
    const [selectedVendedorId, setSelectedVendedorId] = useState(null);
    const [selectedCompradorId, setSelectedCompradorId] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!selectedProductId || !quantidade || !selectedVendedorId || !selectedCompradorId) {
            alert('Por favor, preencha todos os campos');
            return;
        }

        const venda = {
            produto_id: selectedProductId,
            vendedor_id: selectedVendedorId,
            comprador_id: selectedCompradorId,
            quantidade: parseInt(quantidade),  // Certificando-se de que a quantidade seja um número
        };

        try {
            const response = await axios.post('http://127.0.0.1:5000/venda', venda);
            console.log(response.data);
        } catch (error) {
            console.error("Erro ao cadastrar venda: ", error.response.data);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Produto:
                <select onChange={(e) => setSelectedProductId(e.target.value)} value={selectedProductId}>
                    <option value="">Selecione o Produto</option>
                    {/* Aqui você precisa preencher os produtos dinamicamente */}
                    <option value="1">Produto 1</option>
                    <option value="2">Produto 2</option>
                </select>
            </label>
            <br />

            <label>
                Quantidade:
                <input
                    type="number"
                    value={quantidade}
                    onChange={(e) => setQuantidade(e.target.value)}
                />
            </label>
            <br />

            <label>
                Vendedor:
                <select onChange={(e) => setSelectedVendedorId(e.target.value)} value={selectedVendedorId}>
                    <option value="">Selecione o Vendedor</option>
                    {/* Aqui você precisa preencher os vendedores dinamicamente */}
                    <option value="1">Vendedor 1</option>
                    <option value="2">Vendedor 2</option>
                </select>
            </label>
            <br />

            <label>
                Comprador:
                <select onChange={(e) => setSelectedCompradorId(e.target.value)} value={selectedCompradorId}>
                    <option value="">Selecione o Comprador</option>
                    {/* Aqui você precisa preencher os compradores dinamicamente */}
                    <option value="1">Comprador 1</option>
                    <option value="2">Comprador 2</option>
                </select>
            </label>
            <br />

            <button type="submit">Cadastrar Venda</button>
        </form>
    );
};

export default AddSale;
