// src/components/CadastroProduto.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CadastroProduto = () => {
    const [nome, setNome] = useState('');
    const [preco, setPreco] = useState('');
    const [quantidade, setQuantidade] = useState('');
    const [usuarios, setUsuarios] = useState([]); // Lista de usuários
    const [usuarioId, setUsuarioId] = useState(''); // Usuário selecionado

    useEffect(() => {
        // Obter lista de usuários
        axios.get('http://127.0.0.1:5000/usuarios')
            .then(response => setUsuarios(response.data))
            .catch(error => console.error('Erro ao carregar usuários:', error));
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:5000/produto', {
                nome,
                preco,
                quantidade,
                usuario_id: usuarioId // Associar o produto ao usuário selecionado
            });
            console.log(response.data.message);
        } catch (error) {
            console.error('Erro ao adicionar produto:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>Nome do Produto:</label>
            <input type="text" value={nome} onChange={(e) => setNome(e.target.value)} required />
            
            <label>Preço:</label>
            <input type="number" value={preco} onChange={(e) => setPreco(e.target.value)} required />
            
            <label>Quantidade:</label>
            <input type="number" value={quantidade} onChange={(e) => setQuantidade(e.target.value)} required />
            
            <label>Usuário:</label>
            <select value={usuarioId} onChange={(e) => setUsuarioId(e.target.value)} required>
                <option value="">Selecione um usuário</option>
                {usuarios.map((user) => (
                    <option key={user.id} value={user.id}>{user.nome}</option>
                ))}
            </select>
            
            <button type="submit">Adicionar Produto</button>
        </form>
    );
};

export default CadastroProduto;
