// src/components/EditUser.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './EditUser.css'; // Adicione um CSS para estilizar a página de edição

const EditUser = () => {
    const { id } = useParams();
    const [usuario, setUsuario] = useState({
        nome: '',
        email: '',
        cpf_cnpj: '',
        senha: ''
    });

    useEffect(() => {
        const fetchUsuario = async () => {
            const response = await axios.get(`http://127.0.0.1:5000/usuarios/${id}`);
            setUsuario(response.data);
        };
        fetchUsuario();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.put(`http://127.0.0.1:5000/usuarios/${id}`, usuario);
        // Redirecione ou mostre uma mensagem de sucesso
    };

    return (
        <div className="edit-user-container">
            <h2>Editar Usuário</h2>
            <form onSubmit={handleSubmit}>
                {/* Adicione campos de entrada para editar os dados do usuário */}
                <input
                    type="text"
                    value={usuario.nome}
                    onChange={(e) => setUsuario({ ...usuario, nome: e.target.value })}
                    placeholder="Nome"
                />
                <input
                    type="email"
                    value={usuario.email}
                    onChange={(e) => setUsuario({ ...usuario, email: e.target.value })}
                    placeholder="Email"
                />
                <input
                    type="text"
                    value={usuario.cpf_cnpj}
                    onChange={(e) => setUsuario({ ...usuario, cpf_cnpj: e.target.value })}
                    placeholder="CPF/CNPJ"
                />
                <input
                    type="password"
                    value={usuario.senha}
                    onChange={(e) => setUsuario({ ...usuario, senha: e.target.value })}
                    placeholder="Senha"
                />
                <button type="submit">Atualizar</button>
            </form>
        </div>
    );
};

export default EditUser;
