// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import CadastroForm from './components/CadastroForm';
import UserList from './components/UserList';
import EditUser from './components/EditUser';
import CadastroProduto from './components/CadastroProduto';
import EditProduct from './components/EditProduct';
import ListaProduto from './components/ListaProduto';
import AddSale from './components/AddSale'; // Para cadastrar vendas
import SalesList from './components/SalesList'; // Para listar vendas

const App = () => {
    return (
        <Router>
            <div>
                {/* Menu de Navegação */}
                <nav style={styles.navbar}>
                    <ul style={styles.navList}>
                        <li><Link to="/" style={styles.navLink}>Cadastro de Usuário</Link></li>
                        <li><Link to="/usuarios" style={styles.navLink}>Lista de Usuários</Link></li>
                        <li><Link to="/cadastrar-produto" style={styles.navLink}>Cadastro de Produto</Link></li>
                        <li><Link to="/produtos" style={styles.navLink}>Lista de Produtos</Link></li>
                        <li><Link to="/vendas/nova" style={styles.navLink}>Cadastrar Venda</Link></li>
                        <li><Link to="/vendas" style={styles.navLink}>Lista de Vendas</Link></li>
                    </ul>
                </nav>

                {/* Definição de Rotas */}
                <div style={styles.content}>
                    <Routes>
                        <Route path="/" element={<CadastroForm />} />
                        <Route path="/usuarios" element={<UserList />} />
                        <Route path="/editar/:id" element={<EditUser />} />
                        <Route path="/cadastrar-produto" element={<CadastroProduto />} />
                        <Route path="/produtos" element={<ListaProduto />} />
                        <Route path="/editar-produto/:id" element={<EditProduct />} />
                        <Route path="/vendas/nova" element={<AddSale />} />
                        <Route path="/vendas" element={<SalesList />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
};

// Estilos para o Menu e o Conteúdo
const styles = {
    navbar: {
        backgroundColor: '#343a40',
        padding: '10px',
        display: 'flex',
        justifyContent: 'center',
    },
    navList: {
        listStyleType: 'none',
        margin: 0,
        padding: 0,
        display: 'flex',
        gap: '15px',
    },
    navLink: {
        textDecoration: 'none',
        color: '#ffffff',
        fontSize: '16px',
        fontWeight: 'bold',
        transition: 'color 0.3s',
    },
    navLinkHover: {
        color: '#1e90ff',
    },
    content: {
        marginTop: '20px',
        padding: '20px',
        textAlign: 'center',
    },
};

export default App;
